import {fetchAllUserData} from "../../api/getAllUserData"
import { useEffect } from "react";


const ProfileContainer = styled.section`
    display: flex;
    height: 20vh;
    width: 100%;
    justify-content: space-between;

    > .leftDiv {
        display: flex;
        height: 100%;
        align-items: center;
        >img{
            height: 100%;
            border-radius: 20px;
        }
        > .userInfo {
            margin-left: 20px;
            & > :first-child {
                margin: 0;
                font-size: 200%;
                color: var(--black);
            }
            & > :last-child {
                margin: 0;
                color: var(--silver-darker);
        }
         }
    }

    > .rightDiv {
        display: flex;
        align-items: start;
        height: 100%;
        >button{
            padding: 10px;
            margin-left: 20px;
            color: var(--white);
            border-radius: 5px;
        }
    }
`

export default function ProfileHeader(){
    const navigate = useNavigate()
    const dispatch = useDispatch()
    const user = useSelector(state => state.user)
