import { styled } from "styled-components";
import userNull  from "../../asset/User_null.png"
import { useSelector , useDispatch} from "react-redux";
import { useNavigate } from "react-router";
import { useEffect } from "react";
import { fetchAllUser } from "../../api/getAllUser"

const ProfileContainer = styled.section`
    display: flex;
    height: 20vh;
    width: 100%;
    justify-content: space-between;

    > .leftDiv {
        display: flex;
        height: 100%;
        align-items: center;
        >img{
            height: 100%;
            border-radius: 20px;
        }
        > .userInfo {
            margin-left: 20px;
            & > :first-child {
                margin: 0;
                font-size: 200%;
                color: var(--black);
            }
            & > :last-child {
                margin: 0;
                color: var(--silver-darker);
        }
         }
    }

    > .rightDiv {
        display: flex;
        align-items: start;
        height: 100%;
        >button{
            padding: 10px;
            margin-left: 20px;
            color: var(--white);
            border-radius: 5px;
        }
    }
`

export default function ProfileHeader(){
    const navigate = useNavigate()
    const dispatch = useDispatch()
    const localUser = useSelector(state => state.user)
    const path = window.location.pathname;
    const [user, setUser] = useState(null)
    const memberId = path.slice(path.lastIndexOf("/") + 1);
    console.log(user)

    useEffect(() => {
        dispatch(fetchAllUser(10, 10)).then(data => console.log(data));
        //getUser
    },[dispatch])

    return(
        <>
        <ProfileContainer>
            {user
            ?(
                <>
                            <div className="leftDiv">
                <img src={userNull} alt="userNull"/>
                <div className="userInfo">
                    <p>{user.data.name}</p>
                    <p>{user.data.email}</p>
                </div>
            </div>
            <div className="rightDiv">
                {localUser.data.memberId == memberId || user.data.admin
                ? <>
                    <button style={{ backgroundColor: 'var(--blue-500)' }}  onClick={()=>navigate(`/localUser/edit/${localUser.data.memberId}`)}>Edit Profile</button>
                    <button style={{ backgroundColor: 'var(--red-400)' }}  onClick={()=>navigate(`/localUser/delete/${localUser.data.memberId}`)}>Delete Profile</button>
                </>
                :<></>}
            </div>
                </>
            )
        : null}

        </ProfileContainer>
        </>
    )
}
